function banner(title, start, arr, data, cfg) {
    let id = title + 'lunbo';
    var rnum = Math.floor(Math.random() * data.length);
    var item = data[rnum];
    putMyVar('rnum', rnum);
    let time = 5000;
    let col_type = 'pic_1_card';
    let color = "white";
    let desc = '';
    if (cfg != undefined) {
        time = cfg.time ? cfg.time : time;
        col_type = cfg.col_type ? cfg.col_type : col_type;
        desc = cfg.desc ? cfg.desc : desc;
    }

    arr.push({
        col_type: col_type,
        img: item.img,
        desc: desc,
        title: item.title,
        url: item.url,
        extra: {
            id: id+'bar',
        }
    })

    if (start == false || getMyVar('benstart', 'true') == 'false') {
        unRegisterTask(id)
        return
    }

    //log(data)

    let obj = {
        data: data,
    };

    registerTask(id, time, $.toString((obj,id) => {
        var data = obj.data;
        var rum = getMyVar('rnum');

        var i = Number(getMyVar('banneri', '0'));
        if (rum != '') {
            i = Number(rum) + 1
            clearMyVar('rnum')
        } else {
            i = i + 1;
        }
        //log(i)
        //log(data.length)

        if (i > data.length - 1) {
            i = 0
        }
        var item = data[i];
        //log(item)
        try {
            updateItem(id+'bar', {
                title: item.title,
                img: item.img,
                url: item.url,
                extra: {
                    //name: item.title.replace(/<[^>]+>/g, ''),
                    //sname: item.extra.sname,
                    //stype: item.extra.stype,
                    //surl: item.url,
                    //img:item.img,
                    //pageTitle: item.title.replace(/<[^>]+>/g, ''),
                }
            })
        } catch (e) {
            log(e.message)
            unRegisterTask(id)
        }
        putMyVar('banneri', i);

    }, obj,id))
}

