            list.map(x => {
                clearMyVar('er_' + x + 'fold');
                clearMyVar('er_' + x);
            })
            for (let i = 0; i < sorts; i++) {
                clearMyVar('playlist_' + i);
            }
            clearMyVar('er_playlists');
        }, list, sorts))
    },
