                        else if (/\.flv|bili/.test(urls[k])) {
                            return urls[k] + ';{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com}';
                        } else if (/url=/.test(urls[k])) {
                            return urls[k].split('url=')[1];
                        } else {
                            return urls[k] + '#isVideo=true#';
                        }
                    }
                }
            });
        return video;
