    showLoading("验证码识别中，请稍等");

    function 获取验证码(url, ua) { // 验证码链接
        ua = ua || PC_UA;
        let ocr_api = 'http://ocr.nokia.press/dz6/api/ocr';
        let host = url.match(/(.*)\/\/(.*?)\//)[0];
        let headers = {
            'User-Agent': ua,
            'Referer': host,
            'Cookie': getItem('cookie', ""),
        };
        let hex = fetch(url, {
            headers: headers,
            toHex: true,
            timeout: 3000,
            withHeaders: true
        });
        hex = JSON.parse(hex);
        let re = hex.headers["set-cookie"];
        //log(hex.headers);
        let cookie = re || [];
        setItem("cookie", cookie.join(';'));
        hex = hex.body;
        // log('hex:'+hex);
        let ret = request(ocr_api, {
            headers: {
                // 'content-type': 'application/json',
                'content-type': 'application/x-www-form-urlencoded',
            },
            // body: JSON.stringify(data),
            body: 'hex=' + hex,
            method: 'POST'
        });
        try {
            ret = JSON.parse(ret).ret
        } catch (e) {}
        return ret
    }
    var t = new Date();
    let vcode = 获取验证码('http://taoju.tv/scode.php?t=' + t);
    log(vcode);
    //休眠1秒
    //java.lang.Thread.sleep(1000);

    function 提交验证(url, ua, method, body) {
        log(getItem('cookie'))
        ua = ua || PC_UA;
        method = method || 'POST';
        body = body || '';
        let host = url.match(/(.*)\/\/(.*?)\//)[0];

        let headers = {
            'User-Agent': ua,
            'Referer': host,
            'Cookie': getItem('cookie', "")
        };
        let params = {
            headers: headers,
            method: method,
            withHeaders: true
        };
        if (body) {
