eval(fetch(getVar('BteaModel')))
let p={
"title":"h1&&Text;.stui-content__detail&&p,3&&Text",
"img":".lazyload&&data-original",
"url":".m_background&&style",
"desc":".stui-content__detail&&p,1&&Text;.stui-content__detail&&p,2&&Text",
"content":"#desc&&Text",
"tabs":".nav-tabs&&li",
"tab_text":"",
"lists":"body&&.stui-content__playlist",
"tab_id":""
};
var lazy = $('').lazyRule((MY_HOME) => {
log(MY_HOME)
    let url = pdfh(request(input), 'iframe&&src');
    //log(url)
    try {
        var playData = request(url);
        //log(playData)
        let purl = playData.match(/vod" src="(.*?)http(.*?)"/)[2];
        var playUrl = "http" + purl;
    } catch (e) {
        log(e.toString())
        var playUrl = playData.match(/var vid="(.*?)"/)[1];
    }

    function GetPlayUrl(playUrl) {
        if (/mgtv|sohu/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
        } else if (/bili/.test(playUrl)) {
            return playUrl + ";{Referer@https://www.bilibili.com}";
        } else if (/ixigua/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)&&Referer@https://www.ixigua.com/}";
        } else {
            return playUrl + "#isVideo=true#";
        }
    }
    return clearM3u8Ad(GetPlayUrl(playUrl)+'#isM3u8#',{headers:{}})
}, MY_HOME);
二级.A(p,lazy)

